const items = $input.all();
const htmlData = items[0].json;

const titles = htmlData.All_Titles || [];
const links = htmlData.All_Links || [];
const prices = htmlData.All_Prices || [];

console.log('Titles:', titles.length);
console.log('Links:', links.length);  
console.log('Prices:', prices.length);
console.log('Price values:', prices);

const results = [];

// Try to match prices with products
for (let i = 0; i < prices.length; i++) {
    // Find a matching title/link that has a product ASIN
    for (let j = 0; j < links.length; j++) {
        const link = String(links[j] || '');
        const title = String(titles[j] || '');

        if (link.includes('/dp/')) {
            const asinMatch = link.match(/\/dp\/([A-Z0-9]{10})/);
            if (asinMatch) {
                const priceStr = String(prices[i]).replace(/,/g, '').replace(/\./g, '');
                const priceNum = parseInt(priceStr) || 0;

                results.push({
                    json: {
                        Title: title,
                        Link: link.startsWith('http') ? link : 'https://www.amazon.in' + link,
                        Price: priceNum,
                        ASIN: asinMatch[1]
                    }
                });
                break; // Move to next price
            }
        }
    }
}

console.log('Results:', results.length);
return results;

// Get the cleaned product data from Node 4
const products = $('Code in JavaScript').all();

// Rename fields to match Google Sheet headers
return products.map(item => ({
    json: {
        Product_Name: item.json.Title,
        Product_Link: item.json.Link,
        ASIN: item.json.ASIN,
        Original_Price: item.json.Price,
        Current_Price: item.json.Price,
