        Check_Time: new Date().toTimeString().split(' ')[0],
        Price_History: item.json.Price,
        Notes: 'Initial load'
    }
}));

// Get current products from Node 4
const currentProducts = $('Code in JavaScript').all();

// Get previous data from Google Sheets
const previousData = $('Get row(s) in sheet').all();

console.log('Current products:', currentProducts.length);
console.log('Previous data:', previousData.length);

const priceDrops = [];
const updates = [];

// Compare prices
for (const current of currentProducts) {
    const currentData = current.json;

    // Find matching product in sheet by ASIN
    const previous = previousData.find(p => p.json.ASIN === currentData.ASIN);

    if (previous) {
        const oldPrice = parseFloat(previous.json.Current_Price) || 0;
        let newPrice = currentData.Price;

        // ‚ö†Ô∏è TEMPORARY FAKE DROP FOR TESTING ‚ö†Ô∏è
        // Force first product to have a 10% drop
        if (currentData.ASIN === 'B0D78X5CMJ') {
            newPrice = Math.round(oldPrice * 0.90); // 10% drop
            console.log(`üß™ TEST MODE: Forcing fake 10% drop for ${currentData.ASIN}`);
        }

        console.log(`Comparing ${currentData.ASIN}: Old=${oldPrice}, New=${newPrice}`);

        // Check if price dropped (new price is lower)
        if (newPrice < oldPrice && newPrice > 0) {
            const drop = oldPrice - newPrice;
            const dropPercent = parseFloat(((drop / oldPrice) * 100).toFixed(2));

            console.log(`  üî• PRICE DROP! -‚Çπ${drop} (${dropPercent}%)`);

            priceDrops.push({
                json: {
                    Product_Name: currentData.Title,
                    Product_Link: currentData.Link,
                    ASIN: currentData.ASIN,
                    Original_Price: previous.json.Original_Price || oldPrice,
                    Current_Price: newPrice,
                    Previous_Price: oldPrice,
                    Price_Drop_Percent: dropPercent,
                    Check_Date: new Date().toISOString().split('T')[0],
                    Check_Time: new Date().toTimeString().split(' ')[0],
                    Price_History: (previous.json.Price_History || oldPrice) + ',' + newPrice,
                    Notes: `Price dropped from ‚Çπ${oldPrice} to ‚Çπ${newPrice}`
                }
            });
        } else {
            // No drop, but update check time
            updates.push({
                json: {
                    Product_Name: currentData.Title,
                    Product_Link: currentData.Link,
                    ASIN: currentData.ASIN,
                    Original_Price: previous.json.Original_Price,
                    Current_Price: newPrice,
                    Previous_Price: oldPrice,
                    Price_Drop_Percent: 0,
                    Check_Date: new Date().toISOString().split('T')[0],
                    Check_Time: new Date().toTimeString().split(' ')[0],
                    Price_History: previous.json.Price_History,
                    Notes: 'Price checked - no change'
                }
            });
        }
    }
}

console.log(`‚úÖ Price drops found: ${priceDrops.length}`);
console.log(`üìä Updates (no drops): ${updates.length}`);

// Return price drops if any, otherwise return updates
return priceDrops.length > 0 ? priceDrops : updates;

