// Parse AI recommendations with bullet points
function parseAIRecommendations(aiText) {
    const recommendations = {};
    const lines = aiText.split('\n').filter(line => line.trim());

    let currentProduct = null;
    let currentVerdict = '';
    let currentPoints = [];

    for (let i = 0; i < lines.length; i++) {
        const line = lines[i].trim();

        // Match product header with verdict
        const productMatch = line.match(/Product\s+(\d+):\s*([üî•‚≠ê‚è≥]?\s*[A-Z\s]+)/i);
        if (productMatch) {
            // Save previous product if exists
            if (currentProduct) {
                recommendations[currentProduct] = {
                    verdict: currentVerdict,
                    points: currentPoints
                };
            }

            // Start new product
            currentProduct = productMatch[1];
            currentVerdict = productMatch[2].trim();
            currentPoints = [];
        }
        // Match bullet points
        else if (line.startsWith('‚Ä¢') || line.startsWith('-') || line.startsWith('*')) {
            const point = line.replace(/^[‚Ä¢\-*]\s*/, '').trim();
            if (point) {
                currentPoints.push(point);
            }
        }
        // Sometimes AI puts points without bullets
        else if (currentProduct && line.length > 20 && !line.includes('Product')) {
            currentPoints.push(line);
        }
    }

    // Save last product
    if (currentProduct) {
        recommendations[currentProduct] = {
            verdict: currentVerdict,
            points: currentPoints
        };
    }

    return recommendations;
}

