function createSparkline(priceHistory, currentPrice) {
    if (!priceHistory || priceHistory === currentPrice) {
        return '<span style="color:#999;">üìä First Check</span>';
    }

    const prices = priceHistory.toString().split(',').map(p => parseFloat(p)).filter(p => !isNaN(p));
    if (prices.length < 2) {
        return '<span style="color:#999;">üìä First Check</span>';
    }

    const max = Math.max(...prices);
    const min = Math.min(...prices);
    const range = max - min || 1;

    // Create simple text sparkline
    let sparkline = 'üìä ';
    for (let i = 0; i < prices.length; i++) {
        const normalized = (prices[i] - min) / range;
        if (normalized > 0.66) sparkline += '‚ñÅ';
        else if (normalized > 0.33) sparkline += '‚ñÉ';
        else sparkline += '‚ñÖ';
    }

    // Add trend indicator
    const firstPrice = prices[0];
    const lastPrice = prices[prices.length - 1];
    const trend = lastPrice < firstPrice ? 'üìâ Dropping' : lastPrice > firstPrice ? 'üìà Rising' : '‚û°Ô∏è Stable';

    return `<span style="color:#666; font-family:monospace;">${sparkline} ${trend}</span>`;
}

